/*******************************************************************************************************************//**
 * @brief     Initialization function that the user can choose to have called automatically during thread entry.
 *            The user can call this function at a later time if desired using the prototype below.
 *            - void ${module.framework.fx.init_function}(void)
 **********************************************************************************************************************/
void ${module.framework.fx.init_function}(void)
{
    uint32_t  fx_ret_val = FX_SUCCESS;

    #if SF_EL_FX_FORMAT_MEDIA_ENABLE_${module.framework.fx.name}

    ssp_err_t error;

    uint32_t sector_size = ${module.framework.fx.sector_size};
    uint32_t sector_count = ${module.framework.fx.total_sectors};

    #if SF_EL_FX_FORMAT_FULL_MEDIA_${module.framework.fx.name}
        error = SF_EL_FX_Get_MEDIA_Info(${module.framework.fx.requires.sf_el_fx::module.framework.sf_el_fx.name}_cfg.p_lower_lvl_block_media, &sector_size, &sector_count);

        if (sector_count <= ${module.framework.fx.hidden_sectors})
        {
            error = SSP_ERR_MEDIA_FORMAT_FAILED;
        }

        if(error != SSP_SUCCESS)
        {
            ${module.framework.fx.name}_err_callback((void *)&${module.framework.fx.name},&error);
        }

        sector_count -= ${module.framework.fx.hidden_sectors};
    #endif

    if (sizeof(g_media_memory_${module.framework.fx.name}) < ${module.framework.fx.sector_size})
    {
        error = SSP_ERR_MEDIA_FORMAT_FAILED;
        ${module.framework.fx.name}_err_callback((void *)&${module.framework.fx.name},&error);
    }

    /* Format media.  */
    fx_ret_val = fx_media_format(&${module.framework.fx.name}, // Pointer to FileX media control block.
                                 SF_EL_FX_BlockDriver, // Driver entry
                                 &${module.framework.fx.requires.sf_el_fx::module.framework.sf_el_fx.name}_cfg, // Pointer to Block Media Driver
                                 g_media_memory_${module.framework.fx.name}, // Media buffer pointer
                                 sizeof(g_media_memory_${module.framework.fx.name}), // Media buffer size
                                 (CHAR *)"${module.framework.fx.volume_name}", // Volume Name
                                 ${module.framework.fx.fat_count}, // Number of FATs
                                 ${module.framework.fx.dir_count}, // Directory Entries
                                 ${module.framework.fx.hidden_sectors}, // Hidden sectors
                                 sector_count, // Total sectors - Hidden Sectors
                                 sector_size, // Sector size
                                 ${module.framework.fx.cluster_size}, // Sectors per cluster
                                 1, // Heads
                                 1); // Sectors per track

    if (FX_SUCCESS != fx_ret_val)
    {
        error = SSP_ERR_MEDIA_FORMAT_FAILED;
        ${module.framework.fx.name}_err_callback((void *)&${module.framework.fx.name},&error);
    }
    #endif

    fx_ret_val = fx_media_open(
        &${module.framework.fx.name},
        (CHAR *)"${module.framework.fx.name}",
        SF_EL_FX_BlockDriver,
        &${module.framework.fx.requires.sf_el_fx::module.framework.sf_el_fx.name}_cfg,
        g_media_memory_${module.framework.fx.name},
        sizeof(g_media_memory_${module.framework.fx.name}));
    if(fx_ret_val != FX_SUCCESS)
    {
        ${module.framework.fx.name}_err_callback((void *)&${module.framework.fx.name},&fx_ret_val);
    }
}

module.functions:
ssp_err_t SF_EL_FX_Get_MEDIA_Info(sf_block_media_instance_t * p_block_media, uint32_t* sector_size, uint32_t* sector_count)
{
    ssp_err_t ret_val = SSP_SUCCESS;

    ret_val = p_block_media->p_api->open(p_block_media->p_ctrl, p_block_media->p_cfg);
    if(ret_val != SSP_SUCCESS)
    {
        return ret_val;
    }

    /* Get actual sector size from media. */
    ret_val = p_block_media->p_api->ioctl(p_block_media->p_ctrl, SSP_COMMAND_GET_SECTOR_SIZE, sector_size);
    if(ret_val != SSP_SUCCESS)
    {
        return ret_val;
    }

    /* Get actual sector count from media. */
    ret_val = p_block_media->p_api->ioctl(p_block_media->p_ctrl, SSP_COMMAND_GET_SECTOR_COUNT, sector_count);
    if(ret_val != SSP_SUCCESS)
    {
        return ret_val;
    }

    /* Close driver.  */
    ret_val =  p_block_media->p_api->close(p_block_media->p_ctrl);
    return ret_val;
}

module.init:
/** Call initialization function if user has selected to do so. */
#if SF_EL_FX_AUTO_INIT_${module.framework.fx.name}
    ${module.framework.fx.init_function}();
#endif
