module.declarations:
/** GUIX Canvas Buffer */
#if ${module.framework.sf_el_gx.p_canvas.used}
#if (${module.framework.sf_el_gx.display_cfg_inheritance} == 1) /* Inherit Frame Buffer Name from Graphics Screen 1 */
uint8_t ${module.framework.sf_el_gx.name}_canvas[sizeof(${module.framework.sf_el_gx.requires.display::module.driver.display.name}_${module.framework.sf_el_gx.requires.display::module.driver.display.input0.buffer_name}[0])] BSP_ALIGN_VARIABLE_V2(4) BSP_PLACE_IN_SECTION_V2(".${module.framework.sf_el_gx.canvas_section}");
#else /* Inherit Frame Buffer Name from Graphics Screen 2 */
uint8_t ${module.framework.sf_el_gx.name}_canvas[sizeof(${module.framework.sf_el_gx.requires.display::module.driver.display.name}_${module.framework.sf_el_gx.requires.display::module.driver.display.input1.buffer_name}[0])] BSP_ALIGN_VARIABLE_V2(4) BSP_PLACE_IN_SECTION_V2(".${module.framework.sf_el_gx.canvas_section}");
#endif
#endif

/** JPEG Work Buffer */
#if GX_USE_SYNERGY_JPEG
#if (${module.framework.sf_el_gx.jpegbuffer_size})
uint8_t ${module.framework.sf_el_gx.name}_jpegbuffer[${module.framework.sf_el_gx.jpegbuffer_size}] BSP_ALIGN_VARIABLE_V2(64) BSP_PLACE_IN_SECTION_V2(".${module.framework.sf_el_gx.jpegWB_section}");
#endif
#endif

/** GUIX Port module control block instance */
static sf_el_gx_instance_ctrl_t ${module.framework.sf_el_gx.name}_ctrl;

/** GUIX Port module configuration */
static const sf_el_gx_cfg_t  ${module.framework.sf_el_gx.name}_cfg =
{
    /* Display Instance Configuration */
    .p_display_instance    = (display_instance_t *)&${module.framework.sf_el_gx.requires.display::module.driver.display.name},

    /* Display Driver Runtime Configuration */
#if (${module.framework.sf_el_gx.display_cfg_inheritance} == 1) /* Inherit Frame Buffer Name from Graphics Screen 1 */
    .p_display_runtime_cfg = &${module.framework.sf_el_gx.requires.display::module.driver.display.name}_runtime_cfg_bg,
#else /* Inherit Frame Buffer Name from Graphics Screen 2 */
    .p_display_runtime_cfg = &${module.framework.sf_el_gx.requires.display::module.driver.display.name}_runtime_cfg_fg,
#endif

    /* GUIX Canvas Configuration */
#if (${module.framework.sf_el_gx.p_canvas.used})
    .p_canvas        = ${module.framework.sf_el_gx.name}_canvas,
#else
    .p_canvas        = NULL,
#endif

    /* Display Driver Frame Buffer A Configuration */
#if (${module.framework.sf_el_gx.display_cfg_inheritance} == 1) /* Inherit Frame Buffer Name from Graphics Screen 1 */
    .p_framebuffer_a = &${module.framework.sf_el_gx.requires.display::module.driver.display.name}_${module.framework.sf_el_gx.requires.display::module.driver.display.input0.buffer_name}[0], /* Always array[0] is used */
#else /* Inherit Frame Buffer Name from Graphics Screen 2 */
    .p_framebuffer_a = &${module.framework.sf_el_gx.requires.display::module.driver.display.name}_${module.framework.sf_el_gx.requires.display::module.driver.display.input1.buffer_name}[0], /* Always array[0] is used */
#endif

    /* Display Driver Frame Buffer B Configuration */
#if (${module.framework.sf_el_gx.display_cfg_inheritance} == 1) /* Inherit Frame Buffer Name from Graphics Screen 1 */
    #if (${module.framework.sf_el_gx.requires.display::module.driver.display.input0.buffer_number} > 1) /* Multiple frame buffers are used for Graphics Screen 1 */
    .p_framebuffer_b = &${module.framework.sf_el_gx.requires.display::module.driver.display.name}_${module.framework.sf_el_gx.requires.display::module.driver.display.input0.buffer_name}[1], /* Always array[1] is used */
    #else /* Single Frame Buffer is used for Graphics Screen 1 */
    .p_framebuffer_b = NULL,
    #endif
#else /* Inherit Frame Buffer Name from Graphics Screen 2 */
    #if (${module.framework.sf_el_gx.requires.display::module.driver.display.input1.buffer_number} > 1) /* Multiple frame buffers are used for Graphics Screen 2 */
    .p_framebuffer_b = &${module.framework.sf_el_gx.requires.display::module.driver.display.name}_${module.framework.sf_el_gx.requires.display::module.driver.display.input1.buffer_name}[1], /* Always array[1] is used */
    #else /* Single Frame Buffer is used for Graphics Screen 2 */
    .p_framebuffer_b = NULL,
    #endif
#endif

    /* User Callback Configuration */
    .p_callback      = ${module.framework.sf_el_gx.p_callback},

    /* JPEG Work Buffer Configuration */
#if GX_USE_SYNERGY_JPEG
    .p_jpegbuffer    = ${module.framework.sf_el_gx.name}_jpegbuffer,
    .jpegbuffer_size = ${module.framework.sf_el_gx.jpegbuffer_size},
    .p_sf_jpeg_decode_instance = (void *)&${module.framework.sf_el_gx.requires.sf_jpeg_decode::module.framework.sf_jpeg_decode.name},
#else
    .p_jpegbuffer    = NULL,
    .jpegbuffer_size = 0,
    .p_sf_jpeg_decode_instance = NULL,
#endif

    /* Screen Rotation Angle Configuration */
    .rotation_angle  = ${module.framework.sf_el_gx.rotation_angle},

    /* D/AVE 2D Buffer Cache */
    .dave2d_buffer_cache_enabled = ${module.framework.sf_el_gx.dave2d_buffer_cache}
};

/** GUIX Port module instance */
sf_el_gx_instance_t ${module.framework.sf_el_gx.name} =
{
    .p_api = &sf_el_gx_on_guix,
    .p_ctrl = &${module.framework.sf_el_gx.name}_ctrl,
    .p_cfg = &${module.framework.sf_el_gx.name}_cfg
};
