module.header:
/** ADC on ADC Instance. */
extern const adc_instance_t ${module.driver.adc.name};
#ifndef ${module.driver.adc.p_callback}
void ${module.driver.adc.p_callback}(adc_callback_args_t * p_args);
#endif

module.declarations:
#if ${module.driver.adc.scan_end_ipl} != BSP_IRQ_DISABLED
#if !defined(SSP_SUPPRESS_ISR_${module.driver.adc.name}) && !defined(SSP_SUPPRESS_ISR_ADC${module.driver.adc.unit})
SSP_VECTOR_DEFINE_CHAN(adc_scan_end_isr, ADC, SCAN_END, ${module.driver.adc.unit});
#endif
#endif
#if ${module.driver.adc.scan_end_b_ipl} != BSP_IRQ_DISABLED
#if !defined(SSP_SUPPRESS_ISR_${module.driver.adc.name}) && !defined(SSP_SUPPRESS_ISR_ADC${module.driver.adc.unit})
SSP_VECTOR_DEFINE_CHAN(adc_scan_end_b_isr, ADC, SCAN_END_B, ${module.driver.adc.unit});
#endif
#endif
adc_instance_ctrl_t ${module.driver.adc.name}_ctrl;
const adc_cfg_t ${module.driver.adc.name}_cfg =
{
    .unit                = ${module.driver.adc.unit},
    .mode                = ${module.driver.adc.mode},
    .resolution          = ${module.driver.adc.resolution},
    .alignment           = ${module.driver.adc.alignment},
    .add_average_count   = ${module.driver.adc.add_average_count},
    .clearing            = ${module.driver.adc.clearing},
    .trigger             = ${module.driver.adc.trigger},
    .trigger_group_b     = ${module.driver.adc.trigger_group_b},
    .p_callback          = ${module.driver.adc.p_callback},
    .p_context           = &${module.driver.adc.name},
    .scan_end_ipl        = ${module.driver.adc.scan_end_ipl},
    .scan_end_b_ipl      = ${module.driver.adc.scan_end_b_ipl},
    .calib_adc_skip      = ${module.driver.adc.skip_internal_calibration},
};
