{
    .scan_mask           = (uint32_t)((${module.driver.adc.channel_0}) | (${module.driver.adc.channel_1}) | (${module.driver.adc.channel_2}) | (${module.driver.adc.channel_3}) | (${module.driver.adc.channel_4}) | (${module.driver.adc.channel_5}) | (${module.driver.adc.channel_6}) | (${module.driver.adc.channel_7}) | (${module.driver.adc.channel_8}) | (${module.driver.adc.channel_9}) | (${module.driver.adc.channel_10}) | (${module.driver.adc.channel_11}) | (${module.driver.adc.channel_12}) | (${module.driver.adc.channel_13}) | (${module.driver.adc.channel_14}) | (${module.driver.adc.channel_15}) | (${module.driver.adc.channel_16}) | (${module.driver.adc.channel_17}) | (${module.driver.adc.channel_18}) | (${module.driver.adc.channel_19}) | (${module.driver.adc.channel_20}) | (${module.driver.adc.channel_21}) | (${module.driver.adc.channel_22}) | (${module.driver.adc.channel_23}) | (${module.driver.adc.channel_24}) | (${module.driver.adc.channel_25}) | (${module.driver.adc.channel_26}) | (${module.driver.adc.channel_27}) | (${module.driver.adc.channel_temperature}) | (${module.driver.adc.channel_voltage})),
	/** Group B channel mask is right shifted by 32 at the end to form the proper mask */
	.scan_mask_group_b   = (uint32_t)(((${module.driver.adc.channel_0}) | (${module.driver.adc.channel_1}) | (${module.driver.adc.channel_2}) | (${module.driver.adc.channel_3}) | (${module.driver.adc.channel_4}) | (${module.driver.adc.channel_5}) | (${module.driver.adc.channel_6}) | (${module.driver.adc.channel_7}) | (${module.driver.adc.channel_8}) | (${module.driver.adc.channel_9}) | (${module.driver.adc.channel_10}) | (${module.driver.adc.channel_11}) | (${module.driver.adc.channel_12}) | (${module.driver.adc.channel_13}) | (${module.driver.adc.channel_14}) | (${module.driver.adc.channel_15}) | (${module.driver.adc.channel_16}) | (${module.driver.adc.channel_17}) | (${module.driver.adc.channel_18}) | (${module.driver.adc.channel_19}) | (${module.driver.adc.channel_20}) | (${module.driver.adc.channel_21}) | (${module.driver.adc.channel_22}) | (${module.driver.adc.channel_23}) | (${module.driver.adc.channel_24}) | (${module.driver.adc.channel_25}) | (${module.driver.adc.channel_26}) | (${module.driver.adc.channel_27}) | (${module.driver.adc.channel_temperature}) | (${module.driver.adc.channel_voltage})) >> 32),
	.priority_group_a    = ${module.driver.adc.priority_group_a},
	.add_mask            = (uint32_t)((${module.driver.adc.add_mask.channel_0}) | (${module.driver.adc.add_mask.channel_1}) | (${module.driver.adc.add_mask.channel_2}) | (${module.driver.adc.add_mask.channel_3}) | (${module.driver.adc.add_mask.channel_4}) | (${module.driver.adc.add_mask.channel_5}) | (${module.driver.adc.add_mask.channel_6}) | (${module.driver.adc.add_mask.channel_7}) | (${module.driver.adc.add_mask.channel_8})| (${module.driver.adc.add_mask.channel_9}) | (${module.driver.adc.add_mask.channel_10})| (${module.driver.adc.add_mask.channel_11}) | (${module.driver.adc.add_mask.channel_12}) | (${module.driver.adc.add_mask.channel_13}) | (${module.driver.adc.add_mask.channel_14}) | (${module.driver.adc.add_mask.channel_15}) | (${module.driver.adc.add_mask.channel_16}) | (${module.driver.adc.add_mask.channel_17}) | (${module.driver.adc.add_mask.channel_18}) | (${module.driver.adc.add_mask.channel_19}) | (${module.driver.adc.add_mask.channel_20}) | (${module.driver.adc.add_mask.channel_21}) | (${module.driver.adc.add_mask.channel_22}) | (${module.driver.adc.add_mask.channel_23}) | (${module.driver.adc.add_mask.channel_24}) | (${module.driver.adc.add_mask.channel_25}) | (${module.driver.adc.add_mask.channel_26}) | (${module.driver.adc.add_mask.channel_27}) | (${module.driver.adc.add_mask.channel_temperature}) | (${module.driver.adc.add_mask.channel_voltage})),
	.sample_hold_mask    = (uint32_t)((${module.driver.adc.sample_hold_mask.channel_0}) | (${module.driver.adc.sample_hold_mask.channel_1}) | (${module.driver.adc.sample_hold_mask.channel_2})),
	.sample_hold_states  = ${module.driver.adc.sample_hold_states},
};
/* Instance structure to use this module. */
const adc_instance_t ${module.driver.adc.name} = 
{
	.p_ctrl 	   = &${module.driver.adc.name}_ctrl,
	.p_cfg  	   = &${module.driver.adc.name}_cfg,
	.p_channel_cfg = &${module.driver.adc.name}_channel_cfg,
	.p_api  	   = &g_adc_on_adc
};
